package practice;

public class ForLoopTraining {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i < 5; i++) {
			System.out.print("Update the Odd numbers" +i);
			i++;
		}
		
		for (int j = 2; j < 5; j++) {
			System.out.print("Update the even numbers" +j);
			j++;
		}

	}

}
